import React, { useState } from "react";


const Tweet = () =>{
  const [inputText, setText] = useState(" ");
  const [userId, setUserId] = useState(" ");


  const textHandler = (e) => {
    setText(e.target.value);
    localStorage.setItem("tweet", inputText);
  };

  const addTweetHandler = (e) => {
    e.preventDefault()
    setText([
      {
        user: userId,
        tweet: inputText
      },
    ]);
    setText("");
  };


  return (
    <form>
      <textarea
        rows='10'
        cols='70'
        name='text'
        id='tweet'
        maxLength= "140"
        placeholder='Enter tweet...'
        onChange={textHandler}
        value={inputText}
      />
      <button
        onClick={addTweetHandler}
        type="submit"
        className="notes-button"
      />
    </form>

  );    
}


export default Tweet;
