import React, { useState } from 'react';
import "../App.css"

const Users = () => {
  const [user, setUser] = useState('')

  const userHandler = (e) => {
    setUser(e.target.value);
  };

  return(
    <div>
      <textarea
        type="Text"
        value={user}
        onInput={userHandler}
        placeholder="Username..."
      />
    </div>
  )
}

export default Users;
