import React, { useState } from 'react';
import "../App.css"

const Users = (props) => {
  const {user} = props
  const [setUser] = useState=('')

  const userHandler = (e) => {
    setUser(e.target.value);
    localStorage.setItem("UserName", user)
  };

  return(
    <div>
      <div>
        User Name:
      </div>
      <input
        type="Text"
        value={user}
        onInput={userHandler}
      />
    </div>
  )
}

export default Users;
