import React,{ useContext, useEffect } from "react";
import "../App.css"
import List from "./List";
import moment from "moment";
import AppContext from "../context/AppContext";
import Loader from "./Loader"

const Tweet = (props) => {
  const appContext = useContext(AppContext);
  const [text, setText] = useState("");
  const maxLength = 140;

  const textHandler = (e) => {
    appContext.setText(e.target.value);
  };

  const handleClick = (e) => {
    appContext.setIsLoading(true);
    const tweetPostData = {
      content: appContext.text,
      userName: props.user,
      date: moment().format(),
    };

    const request = {
      method: 'POST',
      headers: 
        {
          "Content-Type": "application/json",
        },
      body: JSON.stringify(tweetPostData)  
    };

    fetch("https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet", request)
    .then(response => response.json())
    .then(data => appContext.setTweetData([...appContext.tweetData,data]))
    appContext.setText('');
    setTimeout(()=>{
      // window.location.reload()
      appContext.setIsLoading(false);
    },[500])
  };

  useEffect(() => {
    appContext.setIsLoading(true); 
    fetch("https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet")
    .then((response) => {
      response.json().then(data => {
        localStorage.getItem("profileName",props.user)
        appContext.setTweetData(data.tweets.filter(tweets => tweets.userName === props.user))
         })
    })
    appContext.setIsLoading(false);
  }, [appContext, appContext.user, props.user]);


  useEffect(() => {
    if (appContext.text.length < appContext.maxLength) {
      appContext.setIsDisabled(false);
    } else {
      appContext.setIsDisabled(true);
    } if (appContext.text.length >= appContext.maxLength){
      alert("The tweet can't contain more then 140 chars.") 
    }
  }, [appContext]);

  return (

    <form className="form-container">
      <textarea
        rows='10'
        cols='100'
        name='text'
        className='tweet'
        value={appContext.text}
        maxLength= {appContext.maxLength}
        placeholder='Enter tweet...'
        onInput={textHandler}
      />
   {!appContext.isLoading && (
      <button
        onClick={handleClick}
        disabled={appContext.isDisabled}
        type="submit"
        className={`submit-button submit-button-${appContext.isDisabled}`}>
        Tweet
      </button>
    )}
    {appContext.isLoading && <Loader />}
      <List
        tweetData={appContext.tweetData}
      />
    </form>   
  ); 
}


export default Tweet;
