import React,{ useState, useEffect } from "react";
import List from "./List";
import moment from "moment";
import Loader from "./Loader";

const Tweet = () => {
  const [text, setText] = useState("");
  const [user] = useState("user");
  const maxLength = 141;
  const [isDisabled, setIsDisabled] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [tweetPostData, setTweetData] = useState(() => {
    const localData = localStorage.getItem("tweets");
    return localData ? JSON.parse(localData) : [];}); 

  const textHandler = (e) => {
    setText(e.target.value);
  };

  const handleClick = (e) => {
    setIsLoading(true);
    e.preventDefault();
      const tweetPostData = {
      content: text,
      userName: user,
      date: moment().format(),
    };

    const request = {
      method: 'POST',
      headers: {
          "Content-Type": "application/json",
        },
      body: JSON.stringify(tweetPostData)  
    };

     fetch("https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet", request)
     .then(response => response.json())
     .then(data => {console.log(data)
      setIsLoading(false);
    });
  };

  useEffect(() => {
    if (text.length < maxLength) {
      setIsDisabled(false);
    } else {
      setIsDisabled(true);
    } if (text.length >= maxLength){
      alert("The tweet can't contain more then 140 chars.") 
    }
  }, [text.length]);

  useEffect(() => {
    console.log("isLoading", isLoading);
  }, [isLoading]);

  return (
    <div className="form-container">
      <textarea
        rows='10'
        cols='70'
        name='text'
        className='tweet'
        value={text}
        maxLength= {maxLength}
        placeholder='Enter tweet...'
        onInput={textHandler}
      />
   {!isLoading && (
      <button
        onClick={handleClick}
        disabled={isDisabled}
        type="submit"
        className={`submit-button submit-button-${isDisabled}`}>
        Tweet
      </button>
    )}
    {isLoading && <Loader />}
      <List
        tweetPostData={tweetPostData}
      />
    </div>   
  ); 
}


export default Tweet;
