import React,{ useState, useEffect } from "react";
import List from "./List";
import moment from "moment";

const Tweet = () => {
  const [text, setText] = useState("");
  const [user] = useState("Jonathan");
  const maxLength = 141;
  const [isDisabled, setIsDisabled] = useState(true);
  const [tweetData, setTweetData] = useState(() => {
    const localData = localStorage.getItem("tweets");
    return localData ? JSON.parse(localData) : [];}); 

  const textHandler = (e) => {
    setText(e.target.value);
  };

  const handleClick = (e) => {
    e.preventDefault();
    setTweetData([
      ...tweetData,
      {
        id: Math.floor(Math.random() * 1000),
        user: user,
        text: text,
        date: moment().format()
      },
    ]);
    setText("")

    const tweetPostData = {
      content: text,
      userName: user,
      date: moment().format(),
    };

    const request = {
      method: 'POST',
      headers: {
          "Content-Type": "application/json",
        },
      body: JSON.stringify(tweetPostData)  
    };

     fetch("https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet", request)
     .then(response => response.json())
     .then(data => {console.log(data)});
  };

  useEffect(() => {
    if (text.length < maxLength) {
      setIsDisabled(false);
    } else {
      setIsDisabled(true);
    } if (text.length >= maxLength){
      alert("The tweet can't contain more then 140 chars.") 
    }
  }, [text.length]);

  useEffect(() => {
    localStorage.setItem("tweets", JSON.stringify(tweetData));
  }, [tweetData])



  return (
    <form className="form-container">
      <textarea
        rows='10'
        cols='70'
        name='text'
        className='tweet'
        value={text}
        maxLength= {maxLength}
        placeholder='Enter tweet...'
        onInput={textHandler}
      />

      <button
        onClick={handleClick}
        disabled={isDisabled}
        type="submit"
        className={`submit-button submit-button-${isDisabled}`}>
        Tweet
      </button>

      <List
        tweetData={tweetData}
        setTweetData={setTweetData}
      />
    </form>   
  ); 
}


export default Tweet;
