import React, { useState } from 'react';
import "../App.css"

const Users = () => {
  const [user, setUser] = useState('')

  const userHandler = (e) => {
    setUser(e.target.value);
  };

  return(
    <div>
      <input
        type="Text"
        value={user}
        onInput={userHandler}
        placeholder="Username..."
      />
      <button
        onClick={setUser}
        type="submit"
        className='save-button'>
        Save
      </button>
    </div>
  )
  console.log(userHandler)
}

export default Users;
