import React, { useState } from "react";


const Tweet = () =>{
  const [inputText, setText] = useState(" ")

  const textHandler = (e) => {
    setText(e.target.value);
  };


  return (
    <textarea
    rows='10'
    column='50'
    name='text'
    id='text'
    placeholder='Enter tweet...'
    onChange={(e) => textHandler(e)}
    value={inputText}
  />
  );    
}


export default Tweet;
