import React from 'react';
import { Link } from 'react-router-dom';
import "../App.css"

const Users = ({user}) => {
  const [setUser] = useState("");

  const userHandler = (e) => {
    setUser(e.target.value);
    localStorage.setItem("UserName", user)
  };

  return(
    <div>
      <input
        type="Text"
        value={user}
        onChange={userHandler}
        placeholder="Username..."
      />
    <Link to="/">Home</Link>
    </div>
  )
}

export default Users;
