import React,{ useState, useEffect } from "react";
import "../App.css"
import List from "./List";
import moment from "moment";
import Loader from "./Loader"

const Tweet = () => {
  const [text, setText] = useState("");
  const maxLength = 140;
  const [isDisabled, setIsDisabled] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [tweetData, setTweetData] = useState([])

  const textHandler = (e) => {
    setText(e.target.value);
  };

  const handleClick = (e) => {
    setIsLoading(true);
    const tweetPostData = {
      id: (Math.random()*1000),
      content: text,
      userName: user,
      date: moment().format(),
    };

    const request = {
      method: 'POST',
      headers: {
          "Content-Type": "application/json",
        },
      body: JSON.stringify(tweetPostData)  
    };

    fetch("https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet", request)
    .then(response => response.json())
    .then(data => setTweetData([...tweetData,data]))
    setText('');
    setUser('');
    setTimeout(()=>{
      setIsLoading(false);
    },[500])
  };

  useEffect(() => {
    setIsLoading(true); 
    fetch("https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet")
    .then((response) => {
      response.json().then(data => {
        setTweetData(data.tweets.filter(tweets => tweets.userName === {user}))
         })
    })
    setIsLoading(false);
  }, [user]); 

  useEffect(() => {
    if (text.length < maxLength) {
      setIsDisabled(false);
    } else {
      setIsDisabled(true);
    } if (text.length >= maxLength){
      alert("The tweet can't contain more then 140 chars.") 
    }
  }, [text.length]);

  return (
    <form className="form-container">
      <textarea
        rows='10'
        cols='70'
        name='text'
        className='tweet'
        value={text}
        maxLength= {maxLength}
        placeholder='Enter tweet...'
        onInput={textHandler}
      />
   {!isLoading && (
      <button
        onClick={handleClick}
        disabled={isDisabled}
        type="submit"
        className={`submit-button submit-button-${isDisabled}`}>
        Tweet
      </button>
    )}
    {isLoading && <Loader />}
      <List
        tweetData={tweetData}
      />
    </form>   
  ); 
}


export default Tweet;
