import React from 'react';
import "../App.css"

const Users = ({user,setUser}) => {

  const userHandler = (e) => {
    setUser(e.target.value);
    localStorage.setItem("UserName", user)
  };

  return(
    <div>
      <div >
        User Name:
      </div>
      <input
        type="Text"
        value={user}
        onInput={userHandler}
        placeholder="Username..."
      />
    </div>
  )
}

export default Users;
