import React from 'react';
import "../App.css"

const Users = (props) => {
  const {user,setUser} = props

  const userHandler = (e) => {
    setUser(e.target.value);
    localStorage.setItem("UserName", {user})
  };

  return(
    <div>
      <div>
        User Name:
      </div>
      <input
        type="Text"
        value={user}
        onInput={userHandler}
      />
      <button
        onChange={setUser}
      >
        Submit
      </button>
    </div>
  )
}

export default Users;
