import { useState, useEffect} from "react";
import "../App.css"
import moment from "moment";
import List from "./List"
import Loader from "./Loader"
import { Redirect } from "react-router-dom";

const Tweet = (props) => {
  const [text, setText] = useState("");
  const maxLength = 140;
  const [isDisabled, setIsDisabled] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [tweetData, setTweetData] = useState([]);
  const [redirect, setRedirect] = useState(false);

  const textHandler = (e) => {
    setText(e.target.value);
  };

  const handleClick = (e) => {
    setIsLoading(true);
    const tweetPostData = {
      content: text,
      userName: props.user,
      date: moment().format(),
    };

    const request = {
      method: 'POST',
      headers: 
        {
          "Content-Type": "application/json",
        },
      body: JSON.stringify(tweetPostData)  
    };

    fetch("https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet", request)
    .then(response => response.json())
    .then(data => setTweetData([...tweetData,data]))
    setText('');
    setTimeout(()=>{
      // window.location.reload()
      setIsLoading(false);
    },[500])
  };

  useEffect(() => {
    setIsLoading(true); 
    fetch("https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet")
    .then((response) => {
      response.json().then(data => {
        localStorage.getItem("profileName",props.user)
        setTweetData(data.tweets.filter(tweets => tweets.userName === props.user))
         })
    })
    setIsLoading(false);
  }, [props.user]); 

  useEffect(() => {
    if (text.length < maxLength) {
      setIsDisabled(false);
    } else {
      setIsDisabled(true);
    } if (text.length >= maxLength){
      alert("The tweet can't contain more then 140 chars.") 
    }
  }, [text.length]);

  useEffect (() =>{
    if(!tweetData.user) {
      setIsDisabled(true);
      alert("Please enter a Username to continue")
      {redirect && <Redirect to="/userName" />}
    } else{
      setIsDisabled(false)
    }
  }, [tweetData]);

  return (

    <form className="form-container">
      <textarea
        rows='10'
        cols='100'
        name='text'
        className='tweet'
        value={text}
        maxLength= {maxLength}
        placeholder='Enter tweet...'
        onInput={textHandler}
      />
   {!isLoading && (
      <button
        onClick={handleClick}
        // disabled={isDisabled}
        type="submit"
        className={`submit-button submit-button-${isDisabled}`}>
        Tweet
      </button>
    )}
    {isLoading && <Loader />}
    <List tweetData={tweetData} />
    </form> 
  ); 
}


export default Tweet;
